import React, { useState } from "react";
import axios from "axios";
import { Editor } from "@monaco-editor/react";

function App() {
    const [code, setCode] = useState("// Upload Solidity file or enter GitHub URL to analyze");
    const [file, setFile] = useState(null);
    const [githubUrl, setGithubUrl] = useState("");
    const [annotations, setAnnotations] = useState(null);

    const handleFileUpload = (e) => {
        setFile(e.target.files[0]);
    };

    const analyzeFile = async () => {
        const formData = new FormData();
        if (file) {
            formData.append("file", file);
        }
        if (githubUrl) {
            formData.append("github_url", githubUrl);
        }

        try {
            const response = await axios.post("http://localhost:8000/analyze/", formData);
            setAnnotations(response.data);
        } catch (error) {
            console.error("Error analyzing file:", error);
            alert("Failed to analyze file or GitHub repository");
        }
    };

    return (
        <div>
            <h1>Solidity Smart Annotator</h1>
            <input type="file" accept=".zip,.sol" onChange={handleFileUpload} />
            <input type="text" placeholder="Enter GitHub repo URL" value={githubUrl} onChange={(e) => setGithubUrl(e.target.value)} />
            <button onClick={analyzeFile}>Analyze</button>
            <Editor height="50vh" language="solidity" value={code} onChange={(val) => setCode(val)} />
            {annotations ? <pre>{JSON.stringify(annotations, null, 2)}</pre> : <p>No analysis results yet.</p>}
        </div>
    );
}

export default App;
